(function(){
  const API = {
    tipEl: null,
    wrapEl: null,
    cache: new Map(),

    init(){
      const doc = window.document || document;
      const svg = doc.getElementById('svg1');

      // Bevorzugt: Wrapper .svg-wrap
      // Fallback: Elternknoten von #svg1, sonst body
      this.wrapEl = doc.querySelector('.svg-wrap') ||
                    (svg && svg.parentElement) ||
                    doc.body;

      // Tooltip-Element anlegen (Styling kommt aus CSS .svg-tooltip)
      this.tipEl = doc.createElement('div');
      this.tipEl.className = 'svg-tooltip';
      this.tipEl.setAttribute('role', 'tooltip');
      this.tipEl.setAttribute('aria-hidden', 'true');

      // In den Container hängen (nicht in body, falls Wrapper vorhanden)
      this.wrapEl.appendChild(this.tipEl);
    },

    // Textquelle: <desc>, data-tooltip, inkscape:label/id
    getText(target){
      const desc = target.querySelector && target.querySelector('desc');
      if (desc && desc.textContent && desc.textContent.trim().length) {
        return desc.textContent.trim();
      }
      const dataTip = target.getAttribute && target.getAttribute('data-tooltip');
      if (dataTip) return dataTip;
      const label = target.getAttribute && (target.getAttribute('inkscape:label') || target.id);
      return label || '';  // Fallback
    },

    show(evt, explicitText){
      const target = evt.currentTarget || evt.target;
      const txt = explicitText != null ? explicitText : this.getText(target);

      const tipNode = this.tipEl;
      tipNode.textContent = txt;
      tipNode.style.opacity = '1';
      tipNode.setAttribute('aria-hidden','false');

      // Breite/Höhe werden über CSS begrenzt (max-width/max-height)
      this.positionNearTarget(target);
    },

    hide(){
      const tipNode = this.tipEl;
      tipNode.style.opacity = '0';
      tipNode.setAttribute('aria-hidden','true');
    },

    positionNearTarget(target){
      const tipNode = this.tipEl;

      // Geometrie relativ zum Wrapper berechnen
      const targetRect = target.getBoundingClientRect();
      const wrapRect   = this.wrapEl.getBoundingClientRect();

      const offset = 8;
      // Standard: rechts‑oben neben dem Objekt (RELATIV zum Wrapper)
      let x = (targetRect.right - wrapRect.left) + offset;
      let y = (targetRect.top   - wrapRect.top ) + offset;

      // Tooltipgröße messen
      const tipW = tipNode.offsetWidth;
      const tipH = tipNode.offsetHeight;

      // Grenzen innerhalb des Wrappers
      const clampLeft   = 0;
      const clampTop    = 0;
      const clampRight  = this.wrapEl.clientWidth;
      const clampBottom = this.wrapEl.clientHeight;

      // Rechts-Kante prüfen → ggf. links neben das Objekt
      if (x + tipW + offset > clampRight) {
        x = (targetRect.left - wrapRect.left) - tipW - offset;
      }
      // Untere Kante prüfen → ggf. oberhalb positionieren
      if (y + tipH + offset > clampBottom) {
        y = (targetRect.bottom - wrapRect.top) - tipH - offset;
      }

      // Final clampen innerhalb des Wrappers
      if (x < clampLeft) x = clampLeft + offset;
      if (y < clampTop)  y = clampTop  + offset;
      if (x + tipW > clampRight)  x = clampRight  - tipW - offset;
      if (y + tipH > clampBottom) y = clampBottom - tipH - offset;

      tipNode.style.left = Math.round(x) + 'px';
      tipNode.style.top  = Math.round(y) + 'px';
    },

    // JSON abrufen mit Cache
    async fetchJSON(url) {
      if (this.cache.has(url)) return this.cache.get(url);
      const res = await fetch(url, { mode: 'cors', credentials: 'omit' });
      if (!res.ok) throw new Error('HTTP ' + res.status);
      const data = await res.json();
      this.cache.set(url, data);
      return data;
    },

    /**
     * Lädt JSON und zeigt die gewünschte Property als Tooltip an.
     * @param {Event} evt - Maus-/Touch-Event
     * @param {string} url - RAW-JSON-URL
     * @param {string} key - Property-Name (z.B. "description")
     */
    async fetchAndShowJSON(evt, url, key='description') {
      try {
        // Sofort-Feedback
        this.show(evt, 'Lade…');

        const data = await this.fetchJSON(url);
        const txt = (data && data[key]) ? String(data[key]) : '(Kein Text gefunden)';
        this.show(evt, txt);
      } catch (err) {
        console.error('Tooltip JSON fetch error:', err);
        this.show(evt, 'Fehler beim Laden');
      }
    }
  };

  // global verfügbar machen
  window.SVGTooltip = API;
})();
